// Following Focus API 

class CardView : UIView { 
    var orientation: CardOrientation

    enum CardOrientation {
        case front
        case back
    }

    override func accessibilityElementDidBecomeFocused() {
        self.flip(to: .front)
    } 

		override func accessibilityElementDidLoseFocus() {
        self.flip(to: .back)
    }

// The rest of the classâ€¦
